  const wordEl = document.querySelector(".word");
          const hint = document.querySelector(".hint");
          const rangeEl = document.querySelector(".range-slider");
          const rangeValueEl = document.querySelector(".range-slider-value");
          const wordInputEl = document.querySelector("#input");
          const checkBtnEl = document.querySelector(".check-btn");
          const refreshBtnEl = document.querySelector(".refresh-btn");
  
  
          let wordLength = Number(rangeEl.value)
          let wordobj = {};
          let typedWord = "";
  
          createsRandomWord(words, wordLength)
  
          rangeEl.addEventListener("input", rangeInputHandler)
          wordInputEl.addEventListener("input", wordInputElHandler)
          checkBtnEl.addEventListener("click", checkBtnElHandler)
          refreshBtnEl.addEventListener("click", refreshBtnElHandler)
  
          function createsRandomWord(arr, length) {
              wordobj = getRandomWord(arr, length);
              if (!wordobj) {
                  wordEl.textContent = "";
                  hint.textContent = "";
                  return;
              }
              wordEl.textContent = shuffleLetters(wordobj.word);
              hint.textContent = wordobj.hint;
          }
  
          function getRandomWord(arr, length) {
              const filteredArr = arr.filter(el => (el.word).length === +length)
              return filteredArr[Math.floor(Math.random() * filteredArr.length)]
          }
  
          function shuffleLetters(word) {
              return word.split("").map(value => ({ value, sort: Math.random() }))
                  .sort((a, b) => a.sort - b.sort)
                  .map(({ value }) => value)
                  .join("");
          }
  
          function rangeInputHandler(evt) {
              wordLength = evt.currentTarget.value;
              rangeValueEl.textContent = wordLength;
  
              createsRandomWord(words, wordLength)
          }
  
          function wordInputElHandler(evt) {
              typedWord = evt.currentTarget.value.toLowerCase().trim();
          }
  
          function createAlertBox(message, type) {
              const alertBox = document.createElement("div");
              alertBox.classList.add("alert-box", type);
          
              // Create an image element based on the type of alert
              const image = document.createElement("img");
              switch (type) {
                  case "error":
                      image.src = "/games/wordScramble/img/type.png";
                      break;
                  case "error2":
                      image.src = "/games/wordScramble/img/wrong.png";
                      break;
                  case "success":
                      image.src = "/games/wordScramble/img/correct.png";
                      break;
                  default:
                      // Default image source if type is not recognized
                      image.src = "default-image.jpg";
                      break;
              }
              alertBox.appendChild(image);
          
              // Append the message text to the alert box
              const messageText = document.createElement("span");
              messageText.textContent = message;
              alertBox.appendChild(messageText);
          
              // Append the alert box to the body
              document.body.appendChild(alertBox);
          
              // Set a timeout to remove the alert box after a certain duration
              setTimeout(() => {
                  alertBox.remove();
              }, 800); // Close alert box after 2 seconds
          }
          
          function checkBtnElHandler() {
              if (!typedWord) {
                  createAlertBox("", "error");
                  return;
              } else if (typedWord !== wordobj.word) {
                  createAlertBox("", "error2");
                  return;
              } else {
                  createAlertBox("", "success");
                  wordInputEl.value = "";
                  createsRandomWord(words, wordLength);
              }
          }
          
          function refreshBtnElHandler() {
              wordInputEl.value = "";
              createsRandomWord(words, wordLength);
          }
  